<?phpclass Desktop{    var $conection;    var $db;        function Desktop() {        $this->conection = mysql_connect("localhost", "root", "");        $this->db = mysql_select_db("webdesktop", $this->conection);    }        function ClearCookiesTable($date){        $q = "DELETE FROM cookies WHERE date_end<'".$date."' ";        $result = mysql_query($q, $this->conection);        mysql_free_result($result);         mysql_close($conection);    }    }?>