<?phpclass Home{    var $conection;    var $db;        function Home() {        $this->conection = mysql_connect("localhost", "root", "");        $this->db = mysql_select_db("webdesktop", $this->conection);    }                  function Load(){        $q = "SELECT * FROM test";                $result = mysql_query($q, $this->conection);                while ($row = mysql_fetch_array($result)) {            $array[] = $row;        }        return $array;                //mysql_free_result($result);         //mysql_close($conection);    }    }