<?phpclass User{    var $conection;    var $db;        function User() {        $this->conection = mysql_connect("localhost", "root", "");        $this->db = mysql_select_db("webdesktop", $this->conection);    }                  function getBaseUrl() {        $currentPath = $_SERVER['PHP_SELF'];        $pathInfo = pathinfo($currentPath);        $hostName = $_SERVER['HTTP_HOST'];        $protocol = strtolower(substr($_SERVER["SERVER_PROTOCOL"], 0, 5)) == 'https://' ? 'https://' : 'http://';        $BASE_URL = $protocol.$hostName.$pathInfo['dirname'];        return $BASE_URL;    }}?>