<?phpclass User{    var $conection;    var $db;        function User() {        $this->conection = mysql_connect("localhost", "root", "");        $this->db = mysql_select_db("webdesktop", $this->conection);    }                  function getBaseUrl() {        $currentPath = $_SERVER['PHP_SELF'];        $pathInfo = pathinfo($currentPath);        $hostName = $_SERVER['HTTP_HOST'];        $protocol = strtolower(substr($_SERVER["SERVER_PROTOCOL"], 0, 5)) == 'https://' ? 'https://' : 'http://';        $BASE_URL = $protocol.$hostName.$pathInfo['dirname'];        return $BASE_URL;    }        function getUserIdByCookieValue($userCookie){        $q = "SELECT user_id FROM cookies ";        $q.= "WHERE gid='".$userCookie."' ";        $result = mysql_query($q, $this->conection);        if ($row = mysql_fetch_array($result)) {            $userId = $row['user_id'];            return $userId;        }        mysql_free_result($result);        mysql_close($conection);    }        function saveUserFile($userId, $fileName, $fileType){                $q = "INSERT INTO files ";        $q.= "SET name='".$fileName."', user_id='".$userId."', ";        $q.= "type='".$fileType."', date_add=CURDATE() ";        $result = mysql_query($q, $this->conection);        mysql_free_result($result);        mysql_close($conection);    }    }